import React from "react";
import { Button } from "../Button";
import { Link } from "react-router-dom";
import ReactPlayer from "react-player";
import "./HeroSection.css";

export const Kim_HomePage = ({
  lightBg,
  topLine,
  lightText,
  lightTextDesc,
  headline,
  description,
  buttonLabel,
  img,
  alt,
  imgStart,
}) => {
  return (
    <>
      <div
        className={lightBg ? "home__hero-section" : "home__hero-section darkBg"}
      >
        <div className="container">
          <div
            className="row home__hero-row"
            style={{
              display: "flex",
              flexDirection: imgStart === "start" ? "row-reverse" : "row",
            }}
          >
            <div className="col">
              <div className="home__hero-text-wrapper">
                <div className="top-line">{topLine}</div>
                <h1
                  className={lightText ? "home__heading" : "home__heading dark"}
                >
                  {headline}
                </h1>
                <p
                  className={
                    lightTextDesc
                      ? "home__hero-subtitle"
                      : "home__hero-subtitle dark"
                  }
                >
                  {description}
                </p>
                <div className="download--button">
                  <Link to="/download">
                    <Button buttonSize="btn--wide" buttonColor="blue">
                      {buttonLabel}
                    </Button>
                  </Link>
                </div>
              </div>
            </div>
            <div className="col">
              <div className="home__hero-img-wrapper">
                <img src={img} alt={alt} className="home__hero-img" />
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export const BeatBoi_HomePage = ({
  lightBg,
  lightText,
  lightTextDesc,
  headline,
  description,
}) => {
  return (
    <>
      <div
        className={
          lightBg ? "beatboi__hero-section" : "beatboi__hero-section darkBg"
        }
      >
        <div className="container">
          <div className="row beatboi__hero-row">
            <div className="col">
              <div className="beatboi__hero-vid-wrapper">
                <ReactPlayer url="https://youtu.be/5Xm7bzDn7wk" />
              </div>
            </div>
            <div className="col">
              <div className="beatboi__hero-text-wrapper">
                <h1
                  className={
                    lightText ? "beatboi__heading" : "beatboi__heading dark"
                  }
                >
                  {headline}
                </h1>
                <p
                  className={
                    lightTextDesc
                      ? "beatboi__hero-subtitle"
                      : "beatboi__hero-subtitle dark"
                  }
                >
                  {description}
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export const Download_HomePage = ({
  lightBg,
  lightText,
  lightTextDesc,
  headline,
  buttonLabel,
}) => {
  return (
    <>
      <div
        className={lightBg ? "home__hero-section" : "home__hero-section darkBg"}
      >
        <div className="container">
          <div className="row home__hero-row">
            <div className="col">
              <div className="home__hero-text-wrapper">
                <h1
                  className={lightText ? "home__heading" : "home__heading dark"}
                >
                  {headline}
                </h1>
                <div className="download--button">
                  <Link to="/download">
                    <Button buttonSize="btn--wide" buttonColor="blue">
                      {buttonLabel}
                    </Button>
                  </Link>
                </div>
              </div>
            </div>
            <div className="col">
              <div className="home__hero-img-wrapper">
                <img src={img} alt={alt} className="home__hero-img" />
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

///////////Credits Page///////////////Credits Page///////////////Credits Page/////////Credits Page///////////////////Credits Page//////////Credits Page
///////////Credits Page///////////////Credits Page///////////////Credits Page/////////Credits Page///////////////////Credits Page//////////Credits Page

export const Kiki_CreditsPage = ({
  lightBg,
  lightText,
  lightTextDesc,
  headline,
  description,
}) => {
  return (
    <>
      <div
        className={
          lightBg ? "credits__hero-section" : "credits__hero-section darkBg"
        }
      >
        <div className="container">
          <div className="row credits__hero-row">
            <div className="col">
              <div className="credits__hero-text-wrapper">
                <h1 className={lightText ? "heading" : "heading dark"}>
                  {headline}
                </h1>
              </div>
            </div>
            <div className="col">
              <div className="credits__hero-text-wrapper">
                <p
                  className={
                    lightTextDesc
                      ? "credits__hero-subtitle"
                      : "credits__hero-subtitle dark"
                  }
                >
                  {description}
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};
